class Slot {
    constructor(context, setting) {
        this.context = context;
        this.setting = setting;
    }

    init(callback) {
        let reelItems = [];
        let itemBank = this.setting.itemBank;
        let maximumItemInReel = this.setting.maxItemReels;
        for (let i = 0; i < maximumItemInReel; i++) {
            var indexItemFromBank = this.randomInteger(0, (itemBank.length - 1));
            reelItems[i] = itemBank[indexItemFromBank];
        }

        callback(reelItems);
    }

    randomInteger(min, max) {
        return Math.floor(Math.random() * (max - min + 1)) + min;
    }
}